package tiqr.org.secure;

import com.google.zxing.WriterException;
import org.junit.jupiter.api.Test;

import java.io.IOException;

import static org.junit.jupiter.api.Assertions.assertTrue;

class QRCodeGeneratorTest {

    @Test
    void generateQRCodeImage() throws IOException, WriterException {
        String s = QRCodeGenerator.generateQRCodeImage("https://eduid.nl/tiqrenroll/metadata?enrollment_");
        assertTrue(s.startsWith("data:image/png;base64,"));
    }
}